    switch (value) {
      case "desc-titulo":
        this.filterBooks = this.filterBooks.sort((one, two) => one.titulo > two.titulo ? 1 : -1);
        break;

      case "asc-titulo":
        this.filterBooks = this.filterBooks.sort((one, two) => one.titulo > two.titulo ? -1 : 1);
        break;

      case "desc-editorial":
        this.filterBooks = this.filterBooks.sort((one, two) => one.editorial > two.editorial ? 1 : -1);
        break;

      case "asc-editorial":
        this.filterBooks = this.filterBooks.sort((one, two) => one.editorial > two.editorial ? -1 : 1);
        break;

      default:
        this.filterBooks = this.books;
        break;
    }
  }

};
BooksComponent = __decorate([Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.scss']
